CREATE TABLE booking (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, date_arrival DATE NOT NULL, date_departure DATE NOT NULL, adults SMALLINT UNSIGNED NOT NULL, children SMALLINT UNSIGNED, nights SMALLINT UNSIGNED, guest_name VARCHAR(255) NOT NULL, guest_email VARCHAR(255), guest_telephone VARCHAR(255), guest_wish TEXT, price FLOAT(18, 2), summary TEXT, hash VARCHAR(40), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE booking_package (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, booking_id BIGINT NOT NULL, package_id BIGINT, package_name VARCHAR(255), price FLOAT(18, 2), INDEX hotel_id_idx (hotel_id), INDEX booking_id_idx (booking_id), INDEX package_id_idx (package_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE booking_room (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, booking_id BIGINT NOT NULL, room_category_id BIGINT, room_id BIGINT, room_category_name VARCHAR(255), room_number VARCHAR(32), price FLOAT(18, 2), INDEX hotel_id_idx (hotel_id), INDEX booking_id_idx (booking_id), INDEX room_category_id_idx (room_category_id), INDEX room_id_idx (room_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE child_category (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, name VARCHAR(32), age SMALLINT UNSIGNED, INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE default_image (id BIGINT AUTO_INCREMENT, name VARCHAR(255), title VARCHAR(255), image VARCHAR(255) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE hotel (id BIGINT AUTO_INCREMENT, admin_id BIGINT, name VARCHAR(255) NOT NULL, apihash VARCHAR(40), admin_lang VARCHAR(10) DEFAULT 'en', booking_langs VARCHAR(255) DEFAULT 'en', slug VARCHAR(255), UNIQUE INDEX hotel_sluggable_idx (slug), INDEX admin_id_idx (admin_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE hotel_config (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, keyname VARCHAR(32) NOT NULL, section SMALLINT UNSIGNED DEFAULT 1, datatype VARCHAR(32) DEFAULT 'string', label VARCHAR(255), value VARCHAR(255), is_hidden TINYINT(1) DEFAULT '0', INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE package_translation (id BIGINT, name VARCHAR(255) NOT NULL, description TEXT, lang CHAR(2), PRIMARY KEY(id, lang)) ENGINE = INNODB;
CREATE TABLE package (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, min_stay SMALLINT UNSIGNED DEFAULT 1, max_stay SMALLINT UNSIGNED DEFAULT 99, min_adults SMALLINT UNSIGNED DEFAULT 1, max_adults SMALLINT UNSIGNED DEFAULT 99, min_children SMALLINT UNSIGNED DEFAULT 0, max_children SMALLINT UNSIGNED DEFAULT 99, INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE package_item (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, name VARCHAR(255), per_period VARCHAR(255), per_person VARCHAR(255), is_discount TINYINT(1), INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE package_item2_child_category (package_item_id BIGINT, child_category_id BIGINT, PRIMARY KEY(package_item_id, child_category_id)) ENGINE = INNODB;
CREATE TABLE package_item2_package (package_item_id BIGINT, package_id BIGINT, PRIMARY KEY(package_item_id, package_id)) ENGINE = INNODB;
CREATE TABLE price_item (id BIGINT AUTO_INCREMENT, price_item_type TINYINT UNSIGNED NOT NULL, hotel_id BIGINT NOT NULL, date DATE NOT NULL, price FLOAT(18, 2), room_category_id BIGINT, package_item_id BIGINT, INDEX hotel_id_idx (hotel_id), INDEX room_category_id_idx (room_category_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE room (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, room_category_id BIGINT NOT NULL, number VARCHAR(32) NOT NULL, INDEX room_category_id_idx (room_category_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE room_category_translation (id BIGINT, name VARCHAR(255) NOT NULL, description TEXT, lang CHAR(2), PRIMARY KEY(id, lang)) ENGINE = INNODB;
CREATE TABLE room_category (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, adults SMALLINT UNSIGNED NOT NULL, children SMALLINT UNSIGNED DEFAULT 0, min_persons SMALLINT UNSIGNED NOT NULL, max_persons SMALLINT UNSIGNED NOT NULL, is_active TINYINT(1) DEFAULT '1', INDEX hotel_id_idx (hotel_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE room_gallery (id BIGINT AUTO_INCREMENT, room_category_id BIGINT NOT NULL, image VARCHAR(255) NOT NULL, INDEX room_category_id_idx (room_category_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE room_occupancy (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, room_occupancy_entity_id BIGINT, room_id BIGINT NOT NULL, booking_id BIGINT, info VARCHAR(255), date DATE NOT NULL, is_arrival TINYINT(1), is_departure TINYINT(1), is_occupied TINYINT(1), is_closed TINYINT(1), INDEX hotel_id_idx (hotel_id), INDEX room_id_idx (room_id), INDEX room_occupancy_entity_id_idx (room_occupancy_entity_id), INDEX booking_id_idx (booking_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE room_occupancy_entity (id BIGINT AUTO_INCREMENT, hotel_id BIGINT NOT NULL, booking_id BIGINT, typeid TINYINT UNSIGNED, name VARCHAR(255), description TEXT, INDEX hotel_id_idx (hotel_id), INDEX booking_id_idx (booking_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE text_block_translation (id BIGINT, text TEXT, lang CHAR(2), PRIMARY KEY(id, lang)) ENGINE = INNODB;
CREATE TABLE text_block (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, title VARCHAR(255), application VARCHAR(32), module VARCHAR(32), special_mark VARCHAR(32), is_visible_for_admin TINYINT(1) DEFAULT '0', INDEX section_index_idx (application, module), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, lang VARCHAR(255) DEFAULT 'en', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
